class BSForm(forms.ModelForm):
    def __init__(self, *args, **kwargs):
        super(BSForm, self).__init__(*args, **kwargs)
        for fields in self.fields.values():
            fields.widget.attrs['class'] = 'form-control'

class RoleForm(BSForm):
    class Meta:
        model = Role
        fields = '__all__'
        exclude = ['permission']

class MenuForm(BSForm):
    class Meta:
        model = Menu
        fields = ['name', 'weight', 'icon']

    def __init__(self, *args, **kwargs):
        super(MenuForm, self).__init__(*args, **kwargs)
        self.fields['icon'].weight = forms.widgets.RadioSelect()
        self.fields['icon'] = forms.ChoiceField(widget=forms.RadioSelect, choices=ICON_CHOICES, label='图标')

class PermissionForm(BSForm):
    class Meta:
        model = Permission
        fields = "__all__"

# 批量权限使用的form
class MultiPermissionForm(forms.ModelForm):
    class Meta:
        model = Permission
        fields = ['title', 'url', 'name', 'parent', 'menu']

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        for field in self.fields:
            self.fields[field].widget.attrs.update({"class": "form-control"})
        self.fields['parent'].choices = [(None, '-------')] + list(
                Permission.objects.filter(parent__isnull=True, menu__isnull=False).values_list('id', 'title'))

    def clean(self):
        self._validate_unique = True
        menu = self.cleaned_data.get('menu')
        pid = self.cleaned_data.get('parent')

        if menu and pid:
            raise forms.ValidationError('菜单和父权限同时只能选择一个')
        return self.cleaned_data